import React, { useState, useEffect } from 'react';
import './styles.css'; // Import the CSS file

const initialData = [
  'array1string1',
  'array1string2',
  'array1string3',
  'array1string4',
  'array2string1',
  'array2string2',
  'array2string3',
  'array2string4',
  'array3string1',
  'array3string2',
  'array3string3',
  'array3string4',
  'array4string1',
  'array4string2',
  'array4string3',
  'array4string4',
];

const shuffleArray = (array) => {
  for (let i = array.length - 1; i > 0; i--) {
    const j = Math.floor(Math.random() * (i + 1));
    [array[i], array[j]] = [array[j], array[i]];
  }
  return array;
};

const App = () => {
  const [selectedItems, setSelectedItems] = useState(new Set());
  const [shuffledData, setShuffledData] = useState([]);

  useEffect(() => {
    setShuffledData(shuffleArray([...initialData]));
  }, []);

  const handleButtonClick = (item) => {
    if (selectedItems.has(item)) {
      const updatedSelectedItems = new Set(selectedItems);
      updatedSelectedItems.delete(item);
      setSelectedItems(updatedSelectedItems);
    } else if (selectedItems.size < 4) {
      setSelectedItems(new Set(selectedItems).add(item));
    }
  };

  const isItemSelected = (item) => {
    return selectedItems.has(item);
  };

  return (
    <div>
      <h1>String Arrays Button Grid</h1>
      <div className="grid-container">
        {shuffledData.map((item, index) => (
          <button
            key={index}
            className={isItemSelected(item) ? 'selected' : ''}
            onClick={() => handleButtonClick(item)}
          >
            {item}
          </button>
        ))}
      </div>
      <h2>Selected Items</h2>
      <ul>
        {[...selectedItems].map((item, index) => (
          <li key={index}>{item}</li>
        ))}
      </ul>
    </div>
  );
};

export default App;
