import React, { useState, useEffect } from 'react';
import './styles.css'; // Import the CSS file

const buttonPositions = [
  ['array1string1', 'array1string2', 'array1string3', 'array1string4'],
  ['array2string1', 'array2string2', 'array2string3', 'array2string4'],
  ['array3string1', 'array3string2', 'array3string3', 'array3string4'],
  ['array4string1', 'array4string2', 'array4string3', 'array4string4'],
];

const buttonsByArray = [
  ['array1string1', 'array1string2', 'array1string3', 'array1string4'],
  ['array2string1', 'array2string2', 'array2string3', 'array2string4'],
  ['array3string1', 'array3string2', 'array3string3', 'array3string4'],
  ['array4string1', 'array4string2', 'array4string3', 'array4string4'],
];

const App = () => {
  const [selectedItems, setSelectedItems] = useState(new Set());
  const [checkButtonEnabled, setCheckButtonEnabled] = useState(false);
  const [shouldShake, setShouldShake] = useState(false);

  useEffect(() => {
    setCheckButtonEnabled(selectedItems.size === 4);
  }, [selectedItems]);

  const handleButtonClick = (item) => {
    if (selectedItems.has(item)) {
      const updatedSelectedItems = new Set(selectedItems);
      updatedSelectedItems.delete(item);
      setSelectedItems(updatedSelectedItems);
    } else if (selectedItems.size < 4) {
      setSelectedItems(new Set(selectedItems).add(item));
    }
  };

  const isItemSelected = (item) => {
    return selectedItems.has(item);
  };

  const handleCheckButtonClick = () => {
    const selectedArray = buttonsByArray.find(array => {
      return [...selectedItems].every(item => array.includes(item));
    });

    if (selectedArray) {
      console.log('All selected items belong to the same array:', selectedArray);
      setShouldShake(false); // Stop shaking if previously started
    } else {
      console.log('Selected items do not belong to the same array');
      setShouldShake(true); // Trigger the shaking animation
      setTimeout(() => {
        setShouldShake(false); // Stop shaking after a short delay
      }, 500);
    }
  };

  return (
    <div>
      <div className="heading-wrapper"><h1>♥ Connections ♥</h1></div>
      <div className={`grid-container ${shouldShake ? 'shake' : ''}`}>
        {buttonPositions.map((row, rowIndex) => (
          <div key={rowIndex} className="grid-row">
            {row.map((item, columnIndex) => (
              <button
                key={columnIndex}
                className={isItemSelected(item) ? 'selected' : ''}
                onClick={() => handleButtonClick(item)}
              >
                {item}
              </button>
            ))}
          </div>
        ))}
      </div>
      <div className="check-button-container">
        <button
          className="check-button"
          disabled={!checkButtonEnabled}
          onClick={handleCheckButtonClick}
        >
          Check
        </button>
      </div>
      <h2>Selected Items</h2>
      <ul>
        {[...selectedItems].map((item, index) => (
          <li key={index}>{item}</li>
        ))}
      </ul>
    </div>
  );
};

export default App;
