import React, { useState } from 'react';

const initialData = {
  array1: ['array1string1', 'array1string2', 'array1string3', 'array1string4'],
  array2: ['array2string1', 'array2string2', 'array2string3', 'array2string4'],
  array3: ['array3string1', 'array3string2', 'array3string3', 'array3string4'],
  array4: ['array4string1', 'array4string2', 'array4string3', 'array4string4'],
};

const App = () => {
  const [selectedItems, setSelectedItems] = useState([]);

  const handleCheckboxChange = (e) => {
    const value = e.target.value;
    if (selectedItems.includes(value)) {
      setSelectedItems(selectedItems.filter(item => item !== value));
    } else {
      setSelectedItems([...selectedItems, value]);
    }
  };

  return (
    <div>
      <h1>String Arrays Checkbox List</h1>
      {Object.keys(initialData).map((arrayKey, index) => (
        <div key={index}>
          <h2>Array {index + 1}</h2>
          <ul>
            {initialData[arrayKey].map((item, itemIndex) => (
              <li key={itemIndex}>
                <label>
                  <input
                    type="checkbox"
                    value={item}
                    checked={selectedItems.includes(item)}
                    onChange={handleCheckboxChange}
                  />
                  {item}
                </label>
              </li>
            ))}
          </ul>
        </div>
      ))}
      <h2>Selected Items</h2>
      <ul>
        {selectedItems.map((item, index) => (
          <li key={index}>{item}</li>
        ))}
      </ul>
    </div>
  );
};

export default App;
